import React, { useState } from "react";

export default function SearchBar({ onSearch }) {
  const [city, setCity] = useState("");

  return (
    <form
      onSubmit={(e) => {
        e.preventDefault();
        onSearch(city);
        setCity("");
      }}
    >
      <input
        type='text'
        placeholder='Enter a city'
        onChange={(e) => setCity(e.target.value)}
        value={city}
      />
      <input type='submit' value='Agregar' />
    </form>
  );
}
